import React ,{useState , useEffect} from "react";
import "../style.css";

const Code = () => {
const [searchValue, setSearchValue] = useState("delhi");
const [tempInfo, setTempInfo] = useState("");

const getWeatherInfo = async () =>
{
try
{
let url = `https://api.openweathermap.org/data/2.5/weather?q=${searchValue}&units=metric&appid=32909cb137e7e43fd288d514b88c62a6`;

const res = await fetch(url);
const data = await res.json();

const {temp , pressure , humidity} = data.main;
const{main:weathermood} = data.weather[0];
const{name} = data;
const{speed} = data.wind;
const{country,sunset} = data.sys;

const myNewWeatherInfo = {
  temp,
  pressure,
  humidity,
  weathermood,
  name,
  speed,
  country,
  sunset
}
setTempInfo(myNewWeatherInfo);
}
catch(error)
{
  console.log(error);
}
}

useEffect(() => {
 getWeatherInfo();
}, [])
  return (
    <div>
      <div className="wrap">
        <div className="search">
          <input
            type="search"
            placeholder="search...."
            autoFocus
            id="search"
            className="searchTerm"
            value ={searchValue}
            onChange = {(e) => setSearchValue(e.target.value)}
          />

          <button className="searchButton" type="button" onclick={getWeatherInfo}>
            Search
          </button>
        </div>
      </div>

      {/* card template */}

      <article className="widget">
        <div className="weatherIcon">
          <i className={"wi-day-sunny"}></i>
        </div>

        <div className="weatherInfo">
          <div className="temperature">
            <span>25.5&deg;</span>
          </div>

          <div className="description">
            <div className="weatherCondition">sunny</div>
            <div className="place">Delhi,India</div>
          </div>
        </div>

        <div className="date">{new Date().toLocaleString()}</div>

        {/* our four column section */}

        <div className="extra-temp">
          <div className="temp-info-minmax">
            <div className="two-sided-section">
              <p>
                <i className={"wi wi-sunset"}></i>
              </p>
              <p className="extra-info-leftside">
                12:34 PM <br />
                Sunset
              </p>
            </div>

            <div className="two-sided-section">
              <p>
                <i className={"wi wi-humidity"}></i>
              </p>
              <p className="extra-info-leftside">
                12:34 PM <br />
                Humidity
              </p>
            </div>
          </div>
          <div className="weather-extra-info">
            <div className="two-sided-section">
              <p>
                <i className={"wi wi-humidity"}></i>
              </p>
              <p className="extra-info-leftside">
                12:34 PM <br />
                Humidity
              </p>
            </div>

            <div className="two-sided-section">
              <p>
                <i className={"wi wi-humidity"}></i>
              </p>
              <p className="extra-info-leftside">
                12:34 PM <br />
                Humidity
              </p>
            </div>
          </div>
        </div>
      </article>
    </div>
  );
};

export default Code;
